import React, { useState } from "react";
import "./payment.css";
import { useStateValue } from "./StateProvider";
import CheckoutProducts from "./CheckoutProducts";
import { NavLink } from "react-router-dom";
import { CardElement, useStripe } from "@stripe/react-stripe-js";
import { getbaskettotal } from "./reducer";
import NumberFormat from "react-number-format";
function Payment() {
  const [{ basket, user }, dispatch] = useStateValue();

  const [error, setError] = useStripe(null);
  const [disable, setDisabled] = useStripe(true);

  const handleSubmit = (e) => {};
  const handleChange = (ev) => {
    setDisabled(ev.empty);
    setError(ev.error ? ev.error.message : "");
  };

  return (
    <div className="payment">
      <div className="payment__continer">
        <h1>
          Checkout(<NavLink to="/checkout">{basket?.length}</NavLink>)
        </h1>

        <div className="payment__section">
          <div className="payment__title">
            <h3>Delivery Address</h3>
          </div>
          <div className="payment__address">
            <p>{user?.email}</p>
            <p>123 for gomti lucknow</p>
            <p>wewe road lucknow</p>
          </div>
        </div>
        <div className="payment__section">
          <div className="payment__title">
            <h3>review items and delivery</h3>
            <div className="payment__items">
              {basket.map((item) => (
                <CheckoutProducts
                  id={item.id}
                  title={item.title}
                  image={item.image}
                  price={item.price}
                  rating={item.rating}
                />
              ))}
            </div>
          </div>
        </div>
        <div className="payment__section">
          <div className="payment__items">
            <h3>Payment Method</h3>
          </div>
          <div className="payment__details">
            <form onClick={handleSubmit}>
              <CardElement onChange={handleChange} />
              <div className="payment_priceContainer">
                <NumberFormat
                  renderText={(value) => <h3>Order Total:{value}</h3>}
                  decimalScale={2}
                  value={getbaskettotal(basket)}
                  displayType={"text"}
                  thousandSeparator={true}
                  prefix={"Rs"}
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Payment;
